class GraphDownloadControl {
  constructor({ el, elDOM, enableZoom = false }) {
    this.el = el;
    this.elDOM = elDOM;
    this.enableZoom = enableZoom;
    this.downloadImage = this.downloadImage.bind(this);
    this.init();
  }

  init() {
    this.container = d3.select(this.el).classed("download-control", true);

    this.button = this.container
      .append("button")
      .attr("data-toggle", "tooltip")
      .attr("title", "Download PNG")
      .attr("data-bs-original-title", "Download PNG")
      .attr("aria-label", "Download PNG")
      .on("click", this.downloadImage);

    this.button.append("i").attr("class", "fa fa-download fa-lg");
  }

  downloadImage() {
    this.button.attr("disabled", "");
    htmlToImage
      .toPng(this.elDOM, {
        backgroundColor: "#fff",
        filter: (node) =>
          !node.dataset || !node.dataset.hasOwnProperty("ignore"),
        style: {
          transform: "scale(96%)",
        },
      })
      .then((dataUrl) => {
        const time = d3.timeFormat("%Y-%m-%d %H.%M.%S")(new Date());
        const link = document.createElement("a");
        link.download = `Super Loud ${time}.png`;
        link.href = dataUrl;
        link.click();
        this.button.attr("disabled", null);
      });
  }
}

class GraphLegend {
  constructor({ el, color, labels }) {
    this.el = el;
    this.color = color;
    this.labels = labels;
    this.init();
  }

  init() {
    this.container = d3.select(this.el).attr("class", "legend");

    this.item = this.container
      .selectAll(".item")
      .data(this.color.domain())
      .join("div")
      .attr("class", "item");

    this.input = this.item
      .append("input")
      .attr("class", "item__input")
      .attr("type", "checkbox")
      .attr("checked", "")
      .attr("name", this.el.id)
      .attr("id", (d, i) => `${this.el.id}-${i}`);

    this.label = this.item
      .append("label")
      .attr("class", "item__label")
      .attr("for", (d, i) => `${this.el.id}-${i}`);

    this.label
      .append("div")
      .attr("class", "item__swatch")
      .style("background-color", (d) => this.color(d));

    this.label
      .append("div")
      .attr("class", "item__text")
      .text((d, i) => this.labels[i]);
  }

  get selected() {
    return this.input.filter((d, i, n) => n[i].checked).data();
  }
}

class GraphTooltip {
  constructor({ el, elBounds }) {
    this.el = el;
    this.elBounds = elBounds;
    this.show = this.show.bind(this);
    this.move = this.move.bind(this);
    this.hide = this.hide.bind(this);
    this.init();
  }

  init() {
    this.tooltip = d3.select(this.el).attr("class", "tip");
  }

  show(content) {
    this.tooltip.html(content).classed("is-active", true);
    this.tRect = this.el.getBoundingClientRect();
    this.bRect = this.elBounds.getBoundingClientRect();
  }

  move(x0, y0) {
    const offset = 8;

    let x, y;

    if (x0 < this.bRect.width / 2) {
      x = x0 + offset;
      if (x + this.tRect.width > this.bRect.width) {
        x = this.bRect.width - this.tRect.width;
      }
    } else {
      x = x0 - offset - this.tRect.width;
      if (x < 0) {
        x = 0;
      }
    }

    y = y0 - this.tRect.height / 2;
    if (y < 0) {
      y = 0;
    } else if (y + this.tRect.height > this.bRect.height) {
      y = this.bRect.height - this.tRect.height;
    }

    this.tooltip.style("transform", `translate(${x}px,${y}px)`);
  }

  hide() {
    this.tooltip.classed("is-active", false);
  }
}

